import React, { Component } from 'react'
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';

const SignupSchema = Yup.object().shape({
    marque: Yup.string()
        .required('Required'),
    model: Yup.string()
        .required('Required')
});

class AddVoiture extends Component {
    render() {

        return (
        <div>
            <h2>Insertion d'une nouvelle voiture</h2>
            <Formik
            initialValues={{
                firstName: '',
                lastName: '',
                email: '',
            }}
            validationSchema={SignupSchema}
            onSubmit={values => {
                console.log(values);
            }}
            >
            {({ errors, touched }) => (
                <Form>
                    <div className="mb-2">
                        <label className="block text-gray-500 font-bold mb-1 md:mb-0">
                            Marque
                        </label>
                        <Field 
                            name="marque"
                            className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"/>
                        { errors.marque && touched.marque ? (
                            <div>{errors.marque}</div>
                        ) : null }
                    </div>

                    <div className="mb-2">
                        <label className="block text-gray-500 font-bold mb-1 md:mb-0">
                            Model
                        </label>
                        <Field
                            name="model"
                            className="bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500"/>
                        { errors.model && touched.model ? (
                            <div>{errors.model}</div>
                        ) : null }
                    </div>
                    <button type="submit" className="text-white px-4 py-2 bg-blue-500 hover:bg-blue-400">Sauvegarder</button>
                </Form>
            )}
            </Formik>
        </div>)
    }
}

export default AddVoiture;
